// File: src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom'; // Import Redirect
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard'; // Import the Dashboard component
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // You should manage user authentication state

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/register" component={Register} />
          <Route path="/" exact component={Home} />
          {isLoggedIn ? ( // If the user is logged in, render the Dashboard
            <Route path="/dashboard" component={Dashboard} />
          ) : (
            // If the user is not logged in, redirect to the home page
            <Redirect to="/" />
          )}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
