// File: src/components/Login.js
import React, { useState } from 'react';
import FormField from './FormField'; // Import the FormField component
import './Login.css'; // Import your CSS file

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => setEmail(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle login form submission here (e.g., send data to the server)
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <FormField label="Email" type="email" value={email} onChange={handleEmailChange} />
        <FormField label="Password" type="password" value={password} onChange={handlePasswordChange} />
        <button className="login-button" type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
