// File: src/components/CoursePage.js
import React, { useState } from 'react';
import { Link, Route, Switch, useHistory, useRouteMatch } from 'react-router-dom';
import './CoursePage.css';
import ageGroupImage from '../images/img1.jpeg';

function CoursePage() {
  const history = useHistory();
  const match = useRouteMatch();

  const [selectedDay, setSelectedDay] = useState('');

  const handleDaySubmit = () => {
    // Add logic to handle the form submission
    alert(`Day selected: ${selectedDay}`);
  };

  return (
    <div className="course-container">
      <nav className="top-navbar">
        <div className="navbar-logo">CodeSchool</div>
        <div className="navbar-links">
          <Link to="/login" className="navbar-link">
            Login
          </Link>
          <Link to="/register" className="navbar-link">
            Sign Up
          </Link>
        </div>
      </nav>
      <main className="course-content">
        <Switch>
          <Route path={`${match.path}/enroll`}>
            {/* Original CoursePage content */}
            <h1 className="course-title">Decision Intelligence</h1>
            <div className="course-video-container">
              <video controls className="course-video">
                Your browser does not support the video tag.
              </video>
            </div>
            <div className="course-description">
              {/* Original course description content */}
            </div>
            <div className="instructor-details-horizontal">
              {/* Original instructor details content */}
            </div>
            <button
              className="enroll-button"
              onClick={() => alert('Enrolled!')}
            >
              Enroll Now
            </button>
          </Route>
          <Route path={`${match.path}/choose-day`}>
            {/* New content with the form to choose a day */}
            <h1 className="course-title">Choose Day</h1>
            <form className="day-form" onSubmit={handleDaySubmit}>
              <label>
                Select a Day:
                <select
                  value={selectedDay}
                  onChange={(e) => setSelectedDay(e.target.value)}
                >
                  <option value="">Select Day</option>
                  <option value="Monday">Monday</option>
                  <option value="Tuesday">Tuesday</option>
                  <option value="Wednesday">Wednesday</option>
                  <option value="Thursday">Thursday</option>
                  <option value="Friday">Friday</option>
                </select>
              </label>
              <button type="submit">Submit</button>
            </form>
          </Route>
        </Switch>
      </main>
    </div>
  );
}

export default CoursePage;
